import React, { useState } from 'react';
import {
  Card,
  Input,
  Button,
  Typography,
} from '@material-tailwind/react';


const SignUp = () => {
    const [data, setData] = useState({
        email: '',
        password: '',
        confirmPassword: '',
      });
    
      const handleChange = (e) => {
        const { name, value } = e.target;
        setData({
          ...data,
          [name]: value,
        });
      };
    
      const handleSubmit = async (e) => {
        e.preventDefault();
        if (data.password !== data.confirmPassword) {
          alert("Passwords do not match");
          return;
        }
        // Handle signup logic here (API call)
        alert(`Email: ${data.email}, Password: ${data.password}`);
      };
    
  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-r from-purple-400 to-pink-600">
    <Card className="w-full max-w-md p-6 bg-white rounded-lg shadow-lg">
      <Typography
        variant="h3"
        className="text-center mb-4 text-gray-800 font-bold"
      >
        Create your ArtChat Account
      </Typography>
      <form onSubmit={handleSubmit} className="space-y-4">
      <Input
          size="lg"
          label="Name"
          name="name"
          type="text"
          value={data.email}
          onChange={handleChange}
          placeholder="Enter your email"
          className="focus:ring focus:ring-pink-500"
          required
        />
        <Input
          size="lg"
          label="Email"
          name="email"
          type="email"
          value={data.email}
          onChange={handleChange}
          placeholder="Enter your email"
          className="focus:ring focus:ring-pink-500"
          required
        />
        <Input
          size="lg"
          label="Password"
          name="password"
          type="password"
          value={data.password}
          onChange={handleChange}
          placeholder="Enter your password"
          className="focus:ring focus:ring-pink-500"
          required
        />
        <Input
          size="lg"
          label="Confirm Password"
          name="confirmPassword"
          type="password"
          value={data.confirmPassword}
          onChange={handleChange}
          placeholder="Confirm your password"
          className="focus:ring focus:ring-pink-500"
          required
        />
        <Button
          fullWidth
          className="bg-gradient-to-r from-purple-400 to-pink-600 text-white font-bold"
          type="submit"
        >
          Sign Up
        </Button>
        <Typography className="text-center text-gray-500 mt-4">
          Already have an account? <a href="/signin" className="text-pink-500">Sign In</a>
        </Typography>
      </form>
    </Card>
  </div>
  )
}

export default SignUp
